import 'package:flutter/material.dart';
import 'package:flick_video_player/flick_video_player.dart';
import 'package:video_player/video_player.dart';

class Video extends StatefulWidget {
  Video({Key key}) : super(key: key);

  @override
  _VideoState createState() => _VideoState();
}

class _VideoState extends State<Video> {
  List<Map<String, Object>> _list = [
    {
      'id': "2",
      'name': "Python",
      'videoUrl': "https://youtu.be/woVJ4N5nl_s",
      'thumbnailUrl':
          "data:image/jpeg;base64,",
    },
    {
      'id': "3",
      'name': "Java",
      'videoUrl': "https://youtu.be/eIrMbAQSU34",
      'thumbnailUrl':
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTuX_9dU5fxm3AI1F4qBO9dYfjbMCJkd2_YOw&usqp=CAU",
    },
    {
      'id': "4",
      'name': "Flutter",
      'videoUrl': "https://youtu.be/x0uinJvhNxI",
      'thumbnailUrl':
          "data:image/png;base64,"
    }
  ];
  FlickManager flickManager;
  @override
  void initState() {
    super.initState();
    for (var i = 0; i < _list.length; i++) {
      flickManager = FlickManager(
        videoPlayerController: VideoPlayerController.network(_list[i]['url']),
      );
    }
  }

  @override
  void dispose() {
    flickManager.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(
          "Free Tutorials",
          style: TextStyle(color: Colors.white),
        ),
        centerTitle: true,
        backgroundColor: Theme.of(context).primaryColor,
      ),
      body: Container(
        child: ListView.builder(
          itemCount: _list.length,
          itemBuilder: (BuildContext ctx, int index) {
            return Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Padding(
                  padding: const EdgeInsets.all(12.0),
                  child: Text(
                    _list[index]['name'],
                    style: TextStyle(
                        color: Colors.redAccent,
                        fontStyle: FontStyle.normal,
                        fontSize: 28.0),
                  ),
                ),
                FlickVideoPlayer(flickManager: flickManager),
              ],
            );
          },
        ),
      ),
    );
  }
}
